mapper.update:
update t_account

    update_date = now(),

        school_name = #{schoolName},


        user_name = #{userName},


        user_phone = #{userPhone},


        password = #{password},


        account_expired = #{accountExpired}


        account_locked = #{accountLocked}


where school_code = #{schoolCode} and card_number = #{cardNumber}

mapper.select:
select

from t_account where school_code = #{schoolCode} and card_number = #{cardNumber}
ORDER BY create_date DESC

select

from t_account


        school_id = #{schoolId}


        school_code LIKE CONCAT(CONCAT('%',#{schoolCode}),'%')


        AND school_name LIKE CONCAT(CONCAT('%',#{schoolName}),'%')


        AND user_type = #{userType}


        AND user_name LIKE CONCAT(CONCAT('%',#{userName}),'%')


        AND user_phone LIKE CONCAT(CONCAT('%',#{userPhone}),'%')


        AND card_number = #{cardNumber}


        AND login_name LIKE CONCAT(CONCAT('%',#{loginName}),'%')


        AND login_name_update = #{loginNameUpdate}


        AND account_expired = #{accountExpired}


        AND account_locked = #{accountLocked}



ORDER BY create_date DESC
